mapper.select:
SELECT R.* FROM (SELECT R.R_NO, O.OM_CNTNUM,O.P_NO,O.OM_STATE,O.OM_CANCLE, M.M_ID,R.R_TITLE,R.R_CONTENT,R.R_ID,R.R_RETITLE, R.R_RECONTENT,R.R_YNN,R.R_DATE,R.R_GROUP,R.R_FILESRC,R.R_SCORE, P.P_NAME,P.P_COLOR,P.P_SIZE FROM MEMBER M, REVIEW R, PRODUCT P, ORDER_MEMBER O WHERE R.M_ID=M.M_ID AND R.P_NO=P.P_NO AND R.M_ID=O.M_ID AND P_NAME=#{param3} AND R.R_CONTENT like '%'||#{param2}||'%' AND OM_STATE='구매확정' AND O.OM_CANCLE='N' ORDER BY
R_GROUP DESC R_SCORE DESC, R_GROUP DESC
) R

mapper.update:
UPDATE REVIEW SET R_TITLE=#{param2},
R_CONTENT=#{param3}, R_SCORE=#{param4}, R_FILESRC=#{param5}
WHERE R_NO=#{param1}

UPDATE REVIEW SET R_TITLE=#{param2},
R_CONTENT=#{param3}, R_SCORE=#{param4}
WHERE R_NO=#{param1}

UPDATE REVIEW SET R_ID=#{param2},
R_RETITLE=#{param3}, R_RECONTENT=#{param4, jdbcType=VARCHAR},
R_YNN='y' WHERE R_NO=#{param1}

UPDATE REVIEW SET R_ID='',
R_RETITLE='', R_RECONTENT='',
R_YNN='n' WHERE R_NO=#{param1}

mapper.select:
SELECT P_INFO FROM PRODUCT
WHERE P_NAME=#{param1} AND P_FILESRC=#{param2}
GROUP BY P_INFO
